<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Question extends MY_Controller {    public function __construct()    {        parent::__construct();        $this->load->model('question_model');    }    public function interest($category_id=1)    {        $page_num = 3;        $total_page = $this->question_model->get_page_total($page_num, $category_id, 0);        $page = rand(1,$total_page);         $this->data['questions'] = $this->question_model->get_question(array(            "question_category_id"=>$category_id,            "sort_by"=>'update_time',            'sort_direction'=>'desc',            "pid"=>'NULL',            'limit'=>$page_num,            'offset'=>($page-1)*$page_num        ));         Response::build(0,'ok',$this->data['questions'])->show();    }}?>