<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** *  **/class Answer extends Admin_Controller{        function __construct()    {        parent::__construct();        $this->load->model('answer_model');    }    public function create()    {        $ret = true;        foreach($this->input->post('answer') as $answer)        {            $ret = $ret && $this->answer_model->insert_answer($answer);        }        if($ret)        {            Response::build(0,'ok')->show();        }        else        {            Response::build(-1,"create answer fail")->show();        }    }}?>