<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** *  **/class LoadJson extends Admin_Controller{        function __construct()    {        parent::__construct();        $this->data['sites'] = array(array("type"=>0,"site"=>"http://www.aiwenchan.com/"));        $this->load->model('question_model');        $this->load->model('answer_model');        $this->load->model('result_model');    }    public function index()    {        $this->template->admin_render('admin/loadjson/index', $this->data);    }    public function load()    {        $this->template->admin_render('admin/loadjson/index', $this->data);    }}?>