<?php/** * 媒体管理  * * @author shunping.liu@huangjinqianbao.com * @version $Id$ * @copyright shunping.liu@huangjinqianbao.com, 06 一月, 2016 * @package default **//** * Define DocBlock *//** * k **/class Meta extends CI_Controller {        function __construct()    {        parent::__construct();        $this->load->model('meta_model');        $this->load->helper('url_helper');    }    function index()    {        $data['title'] = "媒体列表页";        $data['metas'] = $this->meta_model->get_metas();        $this->load->view('admin/templates/header', $data);        $this->load->view('admin/meta//index', $data);        $this->load->view('admin/templates/footer');    }    function view($id)    {        $data['title'] = "媒体详情";        $data['meta'] = $this->meta_model->get_meta($id);        $this->load->view('admin/templates/header', $data);        $this->load->view('admin/meta/view', $data);        $this->load->view('admin/templates/footer');    }}?>