<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** *  **/class Result extends Admin_Controller{        function __construct()    {        parent::__construct();        $this->load->model('result_model');    }    public function create()    {        $ret = true;        foreach($this->input->post('results') as $result)        {            if(isset($result['id']) && $result['id']>0)            {                $ret = $ret && $this->result_model->update_result($result);            }            else            {                $ret = $ret && $this->result_model->insert_result($result);            }        }        if($ret)        {            Response::build(0,'ok')->show();        }        else        {            Response::build(-1,"create result fail")->show();        }    //    $this->output->enable_profiler(TRUE);    }}?>