<?php/** *  **/class MY_Model extends CI_Model{        function __construct()    {        parent::__construct();        $this->load->database();    }    function _required($required, $data)    {            foreach($required as $field) if(!isset($data[$field])) return false;                return true;    }    function _default($defaults, $options)    {            return array_merge($defaults, $options);    }}?>