<?phpclass Question_category_model extends CI_Model {    public static $TBNAME = "question_category";    public $id;    public $description;    public $img;    public function __construct()    {        $this->load->database();    }    public function get_all()    {        $query = $this->db->get(self::$TBNAME);        return $query->result_array();    }    public function get_category($category_id)    {        $query = $this->db->get_where(self::$TBNAME, array("id"=>$category_id));        return $query->row_array();    }}?>