<?phpclass Result_model extends MY_Model {    public static $TBNAME = "result";    public $id;//自增id    public $question_id;//    public $score_start;//起始分数    public $score_end;//结束分数    public $show_text_result;//显示给用户的结果    public $show_img_result;//显示给用户的图片    public $show_people_count;//同样答案的人数    public $result_answer_id;//    public $result_question_id;    /*    *创建一个result    */     public function insert_result($options=array())     {         if(!$this->_required(array('question_id'))         {             return false;         }         $qualificationArray = array('description', 'question_id');         foreach($qualificationArray as $qualifier)         {             if(isset($options[$qualifier]))                  $this->db->set($qualifier, $options[$qualifier]);         }         $this->db->insert(self::$TBNAME);         return $this->db->last_insert_id();     }    /*    *读取一个tag    */     public function update_tag($options=array())     {         if(!$this->_required(array('tag_id'), $options)) return false;         $qualificationArray = array('description', 'question_id');         foreach($qualificationArray as $qualifier)         {             if(isset($options[$qualifier]))                  $this->db->set($qualifier, $options[$qualifier]);         }         $this->db->where('id', $options['tag_id']);         $this->db->update(self::$TBNAME);         return $this->db->affected_rows();      }    /**    *删除tag    */     public function delete_tag($options = array())     {         if(!$this->_required(array('tag_id'), $options)) return false;         $this->db->where('id', $options['tag_id']);         $this->db->delete(self::$TBNAME);      }    /*    *读取tag    */     public function get_tag($options=array())     {         // default values         $options = $this->_default(array('sortDirection' => 'asc'), $options);         // Add where clauses to query         $qualificationArray = array('id', 'description', 'question_id');         foreach($qualificationArray as $qualifier)         {             if(isset($options[$qualifier]))                  $this->db->where($qualifier, $options[$qualifier]);         }         if(isset($options['limit']) && isset($options['offset']))              $this->db->limit($options['limit'], $options['offset']);         else if(isset($options['limit'])) $this->db->limit($options['limit']);         // sort         if(isset($options['sortBy']))              $this->db->order_by($options['sortBy'], $options['sortDirection']);         $query = $this->db->get(self::$TBNAME);         if($query->num_rows() == 0) return false;         if(isset($options['id']) )         {             return $query->row(0);         }         else         {             return $query->result();         }     }}?>